mapper.update:
UPDATE test
SET isDelete=1
WHERE id = #{id}

mapper.select:
        SELECT
            EXISTS (
                    SELECT
                        id
                    FROM
                        test
                    WHERE
                    date = #{date}
		AND place = #{place}
	)

        SELECT
            EXISTS (
                    SELECT
                        id
                    FROM
                        test
                    WHERE
                    date = #{date}
		AND teachername=#{teacherName}
	)
